<?php

namespace Codecraft63\CertsignLogin;

const TEST = "CertificadoBean : {'emissor':'AC Certisign RFB G4', 'nome':'INSTITUTO OSWALDO CRUZ DE SEGURIDADE SOCIAL:28954717000191', 'cpf':'', 'cnpj':'28954717000191', 'oab':'', 'validade':'2017-09-29 20:59:59', 'emissao':'2016-09-29 21:00:00', 'email':'institucional@fioprev.org.br', 'serial':'4299F47BF2F7F95A13C7AE36DA5E7625', 'valido':'true', 'certificadoBase64':''}";

class ResponseParseTest extends \PHPUnit_Framework_TestCase
{
    public function testResponseParse()
    {
        $obj = new ResponseParse(TEST);
        $this->assertSame('AC Certisign RFB G4', $obj->emissor());
        $this->assertSame('institucional@fioprev.org.br', $obj->email());
    }

    public function testMethodNotExist()
    {
        $obj = new ResponseParse(TEST);
        $this->assertSame(null, $obj->testNotFound());
    }
}
